package com.example.taxi_full.API;import org.json.JSONObject;import java.io.IOException;import java.io.PrintWriter;import java.net.InetAddress;import java.net.Socket;public class TCPSocket {    public PrintWriter toServer;    public Socket socket;    public void TCP_Conn(int serverPort) throws IOException {        InetAddress serverHost = InetAddress.getByName("45.86.47.12");        socket = new Socket(serverHost, serverPort);        toServer =                new PrintWriter(socket.getOutputStream(),true);    }    public void send(JSONObject data) {        if (toServer != null && socket != null) {            toServer.println(data);        }    }    public void close() throws IOException {        toServer.close();        socket.close();    }    public boolean is_close(){        if (toServer != null && socket != null)            return false;        else            return true;    }}